mapper.insert:
INSERT INTO g_ntc_brd(
       title
     , cn
     , use_yn
) VALUES (
       #{title }
     , #{cn }
     , #{useYn }
)

mapper.select:
UPDATE g_ntc_brd
   SET title    = #{title}
     , cn       = #{cn}
     , mdfcn_dt = NOW()
     , use_yn   = #{useYn}
 WHERE seq = #{seq}

